(302, 1, '2023-08-01 00:55:42', '2023-08-01 00:55:42', '<!-- wp:template-part {\"slug\":\"header\",\"tagName\":\"header\",\"theme\":\"creatio\"} /-->\n\n<!-- wp:group {\"tagName\":\"main\"} -->\n	<main class=\"wp-block-group\">\n		<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"7vw\",\"bottom\":\"7vw\"},\"margin\":{\"top\":\"0px\",\"bottom\":\"0px\"}},\"elements\":{\"link\":{\"color\":{\"text\":\"#0d1f3a\"}}}},\"layout\":{\"inherit\":false,\"wideSize\":\"1240px\",\"contentSize\":\"620px\",\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull has-link-color\" style=\"margin-top:0px;margin-bottom:0px;padding-top:7vw;padding-bottom:7vw\"><!-- wp:heading {\"align\":\"wide\",\"style\":{\"typography\":{\"fontSize\":\"33px\",\"lineHeight\":1.2},\"spacing\":{\"margin\":{\"bottom\":\"3.5vw\"}}}} -->\n<h2 class=\"wp-block-heading alignwide\" style=\"margin-bottom:3.5vw;font-size:33px;line-height:1.2\"><strong>Our Top Destinations</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"16px\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":5282,\"sizeSlug\":\"large\",\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://dotcompatterns.files.wordpress.com/2022/04/ryan-spencer-xkarpkd5lvg-unsplash.jpg?w=1024\" alt=\"\" class=\"wp-image-5282\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"19px\",\"lineHeight\":1.7}}} -->\n<p style=\"font-size:19px;line-height:1.7\"><strong><a href=\"#\">Santorini</a></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"16px\",\"lineHeight\":\"1.7\"}}} -->\n<p style=\"font-size:16px;line-height:1.7\">Santorini was ranked the world\'s top island by many magazines and travel sites, including the Travel+Leisure Magazine, and the BBC.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"16px\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":5283,\"sizeSlug\":\"large\",\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://dotcompatterns.files.wordpress.com/2022/04/hello-lightbulb-yb6wfhbkx40-unsplash.jpg?w=1024\" alt=\"\" class=\"wp-image-5283\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"19px\",\"lineHeight\":\"1.7\"}}} -->\n<p style=\"font-size:19px;line-height:1.7\"><strong><a href=\"#\">Imerovigli</a></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"16px\",\"lineHeight\":\"1.7\"}}} -->\n<p style=\"font-size:16px;line-height:1.7\">Imerovigli is a village on the island of Santorini, adjacent to the north of the island capital Fira. Imerovigli is mostly famous for its beautiful sunset.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"16px\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":5284,\"sizeSlug\":\"large\",\"linkDestination\":\"custom\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://dotcompatterns.files.wordpress.com/2022/04/hello-lightbulb-yc8qqp50bda-unsplash.jpg?w=1024\" alt=\"\" class=\"wp-image-5284\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"19px\",\"lineHeight\":\"1.7\"}}} -->\n<p style=\"font-size:19px;line-height:1.7\"><strong><a href=\"#\">Oia</a></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"16px\",\"lineHeight\":\"1.7\"}}} -->\n<p style=\"font-size:16px;line-height:1.7\">Oia was the centre of the story in the 1982 film Summer Lovers. Michael and Cathy, two characters in the film, lived a villa in Oia.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n	</main>\n<!-- /wp:group -->', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2023-08-01 00:55:42', '2023-08-01 00:55:42', '', 0, 'https://superakerele.wordpress.com/2023/08/01/home/', 0, 'wp_template', '', 0),
